/** 审批动作类型 */
enum APPROVE_ACTION {
  /** 下载审批 */
  DOWNLOAD = 'DOWNLOAD_APPROVE',
  /** 推送/邮报审批 */
  DATAPUSH = 'DATAPUSH_APPROVE',
}
/** 枚举审批状态 */
const APPROVE_STATUS = {
  /** 无需审批 */
  NOT_NEED: {
    CODE: 'NO_NEED',
    TEXT: '无需审批',
  },
  /** 待审批 */
  PENDING: {
    CODE: 'NO',
    TEXT: '待发起审批',
  },
  /** 审批中 */
  APPROVING: {
    CODE: 'APPROVING',
    TEXT: '审批中',
  },
  /** 审批通过 */
  PASSED: {
    CODE: 'ALL',
    TEXT: '审批通过',
  },
  /** 驳回审批 */
  REJECT: {
    CODE: 'REJECT',
    TEXT: '驳回审批',
  },
  /** 撤销审批 */
  REVOKE: {
    CODE: 'CANCEL ',
    TEXT: '撤销审批',
  },
}
/** 需要审批的状态码 */
const NEED_APPROVE_STATUS = [APPROVE_STATUS.PENDING.CODE, APPROVE_STATUS.REJECT.CODE, APPROVE_STATUS.REVOKE.CODE]
/** 已审批成功，完成审批，不需要再次审批的状态码 */
const APPROVE_SUCCESS_STATUS = [APPROVE_STATUS.PASSED.CODE, APPROVE_STATUS.NOT_NEED.CODE]

export { APPROVE_ACTION, APPROVE_STATUS, NEED_APPROVE_STATUS, APPROVE_SUCCESS_STATUS }
