interface Window {
  secDBDownload: {
    downloadFile: (url: string, misid: string) => void
  }
  Owl: any
  // 有的版本是大O，有的版本是小O
  owl: any
  // sso clientID
  __SSO_CLIENTID__: string
  __ENV__: string
}

// 解析Promise类型，例如：type a = PromiseType<Promise<k>> ===>  a === k
type PromiseType<T extends Promise<any>> = T extends Promise<infer P> ? P : any
