'use strict'

let ENABLE_CCAT = true  // 临时先关闭c的实现

if (ENABLE_CCAT) {
    // mv ccat lib
    try {
        exports.implement = require('./implements/ccat')
    } catch (e) {
        require('./logger')('adapter').error(e)
        require('./logger')('adapter').info('try use ccat fail , switch to node cat implement')
        exports.implement = require('./implements/nodecat')
    }
} else {
    exports.implement = require('./implements/nodecat')
}
