module.exports = function(filename) {
    var file = '(' + filename + ') '
    return {
        error: function(msg) {
            require('@dp/logger-container')('Cat').error(file + msg)
        },
        info: function(msg) {
            require('@dp/logger-container')('Cat').info(file + msg)
        },
        warn: function(msg) {
            require('@dp/logger-container')('Cat').warn(file + msg)
        }
    }
}
