from .container import sdk


__all__ = ['MetricHelper', 'metric']


class MetricHelper(object):

    def __init__(self, name):
        self._helper = sdk().new_cat_build_metric_helper(name)

    def add_tag(self, key, val):
        sdk().add_metric_tag(self._helper, key, val)
        return self

    def add_name(self, name):
        sdk().add_metric_name(self._helper, name)
        return self

    def count(self, count=1):
        sdk().add_metric_count(self._helper, count)

    def duration(self, duration_ms):
        sdk().add_metric_duration(self._helper, duration_ms)


def metric(name):
    return MetricHelper(name)
