from .const import CAT_SUCCESS
from .event import log_exception
from .heartbeat import _build_heartbeat
from .transaction import Transaction
from .utils import sleep_2_next_min


def run_monitor():
    def once():
        t = Transaction("System", "Status")
        try:
            _build_heartbeat()
            t.set_status(CAT_SUCCESS)
        except Exception as e:
            raise e
            log_exception(e)
        finally:
            t.complete()

    while True:
        try:
            once()
        finally:
            sleep_2_next_min()
