import time

import cat
import threading

cat.initCatClient("my-test")

def thread():
    while True:
        trans = cat.newTransaction('system', 'test')
        cat.setTransactionStatus(trans, '0')
        cat.completeTransaction(trans)
        time.sleep(100)

th = threading.Thread(target=thread, name="cat_heartbeat")
th.setDaemon(True)
th.start()

time.sleep(10000000)
