#ifndef CAT_CLIENT_C_MESSAGE_TREE_H
#define CAT_CLIENT_C_MESSAGE_TREE_H

#include <stdint.h>
#include "lib/cat_sds.h"

#include "cat/message.h"

typedef struct _CatMessageTree {
    CatMessage* root;

    catsds messageId;
    catsds parentMessageId;
    catsds rootMessageId;

    catsds threadGroupName;
    catsds threadId;
    catsds threadName;

    int canDiscard; // determine if the message can be discarded.
} CatMessageTree;

CatMessageTree* newCatMessageTree();

CatMessageTree* duplicateCatMessageTree(CatMessageTree* tree);

void deleteCatMessageTree(CatMessageTree* tree);

#endif //CAT_CLIENT_C_MESSAGE_TREE_H
