#ifndef CCAT_MONITOR_COLLECTOR_H
#define CCAT_MONITOR_COLLECTOR_H

typedef struct loadavg {
    double avg1;
    double avg5;
    double avg15;
} loadavg;

typedef struct cpustat {
    long user;
    long nice;
    long system;
    long idle;
    long iowait;
    long irq;
    long softirq;

    long intr;
    long context;
    long proc_running;
    long proc_blocked;
} cpustat;

typedef struct cpuinfo {
    long user;
    long nice;
    long system;
    long idle;
    long iowait;
    long irq;
    long softirq;
    long total;

    long intr;
    long context;
} cpuinfo;

typedef struct meminfo {
    long total;
    long free;
    long buffers;
    long cached;
    long swap_total;
    long swap_free;
} meminfo;

char *get_status_report();

#endif //CCAT_MONITOR_COLLECTOR_H
