#include <gtest/gtest.h>

#include <cat/client.h>

int main(int argc, char* argv[])
{
    DEFAULT_CCAT_CONFIG.eventBatchQueueCapacity = 100;
    DEFAULT_CCAT_CONFIG.transactionBatchQueueCapacity = 100;
    catClientInitWithConfig("ccat", &DEFAULT_CCAT_CONFIG);
    testing::InitGoogleTest(&argc, argv);
    int res = RUN_ALL_TESTS();
    usleep(3 * 1000 * 1000);
    catClientDestroy();
    return res;
}
