const fs = require('fs');
const path = require('path');
const distRoot = 'dist';
const fn = function (num) {
    const filePath = path.join(__dirname, distRoot);
    if (!fs.existsSync(filePath)) {
        fs.mkdirSync(filePath);
    }
    function c (key) {
        fs.writeFileSync(path.join(__dirname, `./${distRoot}/test${key}.json`), `text${key}`);
    }
    for(let i = 0; i < num; i++) {
        c(i)
    }
}
module.exports = fn;