<template>
  <div :title="item[props.title]" class="tree-list-view" :class="{ border: item.level === 1, active: item.active }">
    <div
      class="tree-list-view-left"
      :style="{
        paddingLeft: item.level - 1 ? 20 * (item.level - 1) + 10 + 'px' : '10px',
        height: option.itemHeight + 'px',
        width: option.treeLeftWidth || '50%'
      }"
    >
      <div class="tree-expand-icon-wrap">
        <!-- 非异步加载 -->
        <i
          class="mtdicon mtdicon-triangle-right auth-apply-mtdicon-triangle-right"
          :class="{
            expandTransform: item.expand,
            expandTransformReturn: !item.expand
          }"
          @click.stop="$emit('toggle-expand', item)"
          v-if="!loadData && item.childLen"
        />
        <!-- 异步加载时，未点击，或者点击加载完毕并且有数据 -->
        <i
          class="mtdicon mtdicon-triangle-right auth-apply-mtdicon-triangle-right"
          :class="{
            expandTransform: item.expand,
            expandTransformReturn: !item.expand
          }"
          @click.stop="$emit('toggle-expand', item)"
          v-if="loadData && !item.loading && (item.hasAsyncChildren === undefined || item.hasAsyncChildren === 1)"
        />
        <i v-if="item.loading" class="mtdicon mtdicon-loading"></i>
      </div>

      <!-- 禁用且有禁用文案 -->
      <mtd-tooltip
        v-if="checkable && item[props.checkable] && item[props.disableCheckbox] && item[props.disableCheckboxHoverText]"
        :content="item[props.disableCheckboxHoverText]"
        placement="top"
      >
        <mtd-checkbox
          v-model="item[props.checked]"
          :indeterminate="item[props.indeterminate]"
          :disabled="item[props.disableCheckbox]"
        ></mtd-checkbox>
      </mtd-tooltip>
      <!-- 禁用 -->
      <mtd-checkbox
        v-if="
          checkable && item[props.checkable] && item[props.disableCheckbox] && !item[props.disableCheckboxHoverText]
        "
        v-model="item[props.checked]"
        :indeterminate="item[props.indeterminate]"
        :disabled="item[props.disableCheckbox]"
      ></mtd-checkbox>
      <!-- 未禁用 -->
      <mtd-checkbox
        v-if="checkable && item[props.checkable] && !item[props.disableCheckbox]"
        v-model="item[props.checked]"
        :indeterminate="item[props.indeterminate]"
        :disabled="treeDisabled"
        @click.native="stopDefault($event)"
        @input="$emit('toggle-checked', item)"
      ></mtd-checkbox>

      <slot v-bind:treeNode="item">
        <div class="tree-list-content">
          {{ item[props.title] }}
        </div>
      </slot>
    </div>
    <div class="tree-list-view-right">
      <slot name="treeNodeCenter" v-bind:treeNode="item"></slot>
      <!-- 父节点开启继承，则子孙节点继承按钮不展示 -->
      <!-- 审批详情：资源不可选，则其父辈全部不展示继承按钮 -->
      <div v-if="this.inheritable && item.showInherit">
        <mtd-switch
          v-model="item.inherit"
          size="small"
          :disabled="treeDisabled || item.disableInherit || item[props.disableCheckbox]"
          @change="$emit('toggle-inherit', item)"
        ></mtd-switch>
        <span class="inherit-text">{{ item.inherit ? '已开启' : '未开启' }}</span>
      </div>
      <div v-if="this.inheritable && !item.showInherit"></div>
      <slot name="treeNodeRight" v-bind:treeNode="item"></slot>
    </div>
  </div>
</template>

<script lang="ts">
import { Component, Prop } from 'vue-property-decorator';
import TreeNode from '../../Tree/TreeNode.vue';

@Component
export default class TableTreeNode extends TreeNode {
  @Prop(Boolean)
  public inheritable!: boolean; // 是否展示继承
}
</script>

