<template>
  <div class="apply-reason">
    <div class="apply-reason-title">
      <span class="title">申请理由</span>
    </div>
    <div class="input-wrap">
      <mtd-textarea
        ref="text"
        placeholder="请详细描述使用场景，以提高申请通过率"
        v-model="inputValue"
        style="width: 100%"
        rows="3"
        maxlength="500"
      ></mtd-textarea>
    </div>
  </div>
</template>

<script>
export default {
  name: 'applyReason',
  props: {
    reason: {
      type: String,
      default: ''
    }
  },
  computed: {
    inputValue: {
      get() {
        return this.reason;
      },
      set(val) {
        this.$emit('update:reason', val);
      }
    }
  }
};
</script>

<style lang="scss">
.input-wrap {
  width: 100%;
}
</style>
