<template>
  <mtd-modal class="context-modal" v-model="viewModal" :title="data.resourceName">
    <div class="graphicDesc" v-text="data.graphicDesc"></div>
    <img v-if="data.graphicUrl" class="graphicImg" :src="data.graphicUrl" />
  </mtd-modal>
</template>

<script>
export default {
  name: 'ContextModal',
  props: {
    data: {
      type: Object,
      defalut: false
    },
    visible: {
      type: Boolean,
      defalut: false
    }
  },
  computed: {
    viewModal: {
      get() {
        return this.visible;
      },
      set(val) {
        this.$emit('update:visible', val);
      }
    }
  },
  methods: {}
};
</script>

<style lang="scss">
.context-modal {
  .mtd-modal {
    max-width: 700px;
  }
  .graphicDesc {
  }
  .graphicImg {
    margin-top: 10px;
    width: 100%;
    height: auto;
    border: 1px solid #f4f4f4;
    border-radius: 4px;
  }
}
</style>
