<template>
  <div class="login-model">
    <iframe class="iframe" :src="iframeSrc" />
  </div>
</template>

<script>
export default {
  name: 'login-model',
  props: {
    env: {
      required: true,
      type: String,
      default: 'dev'
    }
  },
  computed: {
    iframeSrc() {
      const { env } = this;
      const requestUrl = {
        local: 'http://localhost:3000/',
        development: 'http://auth.bi.dev.sankuai.com/',
        test: 'http://auth.bi.test.sankuai.com/',
        staging: 'http://auth.bi.st.sankuai.com/',
        production: `${window.location.protocol}//dt.sankuai.com/auth/`
      };
      return requestUrl[env];
    }
  },
  methods: {}
};
</script>
