<template>
  <div class="valid-time-select">
    <div class="valid-time-title">
      <span class="title">有效期</span>
    </div>
    <div class="valid-time-title-wrap">
      <mtd-radio-group v-model="selectKey" class="radio-wrap">
        <mtd-radio value="2d">1天</mtd-radio>
        <mtd-radio value="7d">7天</mtd-radio>
        <mtd-radio value="1m">1个月</mtd-radio>
        <mtd-radio value="3m">3个月</mtd-radio>
        <mtd-radio value="6m">6个月</mtd-radio>
        <mtd-radio value="1y">1年</mtd-radio>
        <mtd-radio value="2y">2年</mtd-radio>
      </mtd-radio-group>
    </div>
  </div>
</template>

<script>
export default {
  name: 'validTimeSelect',
  props: {
    expireTime: {
      required: false,
      type: String,
      defualt: '6m'
    }
  },
  computed: {
    selectKey: {
      get() {
        return this.expireTime;
      },
      set(val) {
        this.$emit('update:expireTime', val);
      }
    }
  }
};
</script>
