import dataReport from '@datafe/data-report';
export const DATA_REPORT_CID = 'c_techportal_pu6o9i4n';

export default function getReportInstance(env) {
  dataReport.init({
    // 业务指标SDK配置
    biz: {
      enableMonitor: true, // 是否开启监控大盘
      appMonitor: 'auth-user', // 监控大盘上报标识, 可自定义
      prodMode: env === 'production', // 灵犀上报环境
      router: [
        // 路径归类配置, 针对path中有特殊唯一标识的情况, 可不填
        {
          path: '/auth-apply-modal',
          cnName: '权限申请组件'
        }
      ]
    },
    tech: {
      // 技术指标SDK配置
    }
  });
}
