export namespace Cat {
  /**
   *
   * 业务需要上报的项目名称，尽可能早调用该函数。目前的项目上报地址按照优先级：
   * 业务setCatAppKey > 通过环境变量注入 (process.env.__appkey__) > /data/webapps/appenv 文件内容
   * 一般情况下该函数不需要被调用，业务只需要尽可能早地初始化Cat就可以了
   *
   * @param appkey 上报到Owl的项目名称，通常是appKey
   *
   */
  function setCatAppKey(appkey: string): void;

  /**
   *
   * 初始化Cat，正常情况下该函数不要调用或者尽可能晚的调用
   * Node上Cat只能上报到一个AppKey，避免出现该函数调用早导致业务上报出错的情况
   *
   */

  function initCatAppKey(): any;
}
