import SCWeb from './index';

// @ts-ignore
const sc = SCWeb({
    componentId: 'sc-demo', // 组件id，必须
    accessEnv: 'test', // 访问环境，必须，可选值： production,staging,test,development
    loginImmediately: false
});

sc.fetch({
    url: '/demo/api/data',
    method: 'GET'
}).then((res) => {
    const nd = document.querySelector('#content');
    if (nd) {
        nd.innerHTML = `
            <p>${res.data.data.join(',')}</p>
            <p>${res.data.message}</p>
        `;
    }
});