<template>
  <div id="app">
    <img src="./assets/logo.png">
    <p>ssoid:{{ ssoid }}</p>
    <button id="logout" @click="logout" >登出</button>
    <button id="mock401" @click="mock401Rep" >模拟请求业务接口返回401</button>
  </div>
</template>

<script>
const mockMyApi401 = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        status: 401,
        data: {
          message: 'auth failed'
        }
      })
    }, 1000)
  })
}

export default {
  name: 'App',
  props: {
    client: {
      type: Object,
      default: null
    },
    ssoid: {
      type: String,
      default: null
    }
  },
  methods: {
    async logout () {
      await this.client.logout()
      const url = this.client.getLoginUrl()
      location.href = url
    },
    async mock401Rep () {
      const result = await mockMyApi401()
      if (result.status === 401) {
        location.href = this.client.getLoginUrl()
      }
    }
  }
}
</script>

