import http from '../lib/http'

export default {
  alive() {
    return http('/alive')
      .then(res => {
        // eslint-disable-next-line no-console
        console.log('[ @hfe/common-cli-plugin-axios ] Talos Demo Public API Response: ', res)
        return res
      })
      .catch(e => {
        // eslint-disable-next-line no-console
        console.error(e)
        throw e
      })
  },
}
