import * as State from './store/state';
import * as Token from './store/token';
import * as Verifier from './store/verifier';

interface StorageArgs extends State.StateArgs, Token.TokenArgs, Verifier.VerifierStorageArgs {
}

export class Storage {
    stateStore: State.StateStorage;
    tokenStore: Token.TokenStorage;
    verifier: Verifier.VerifierStorage;

    constructor(storageArgs: StorageArgs) {
        this.stateStore = new State.StateStorage({
            clientId: storageArgs.clientId,
        });

        this.tokenStore = new Token.TokenStorage({
            clientId: storageArgs.clientId,
        });

        this.verifier = new Verifier.VerifierStorage({
            method: storageArgs.method,
        });
    }
}
