interface StateObj {
    setState(state: string): void;
    getState(): string;
    clearState(): void;
}

export interface StateArgs {
   clientId: string;
}

export class StateStorage implements StateObj {
    private STATE_POSTFIX_NAME = 'state';
    private STATE_NAME: string;

    constructor(stateArgs: StateArgs) {
        this.STATE_NAME = `${stateArgs.clientId}_${this.STATE_POSTFIX_NAME}`;
    }
    
    setState(state: string) {
        sessionStorage.setItem(this.STATE_NAME, state);
    }

    getState() {
        return sessionStorage.getItem(this.STATE_NAME);
    }

    clearState() {
        sessionStorage.removeItem(this.STATE_NAME);
    }
}