export interface tokenCookieArgs {
    name: string;
    value: string;
    'max-age'?: string;
    [cookieArgs: string]: string|boolean;
}

export function parseCookie(tokenArgs: tokenCookieArgs): string {
    let tokenStr: string = '';
    const { name, value, ...optionalArgs } = tokenArgs;
    tokenStr = `${name}=${value};`;

    Object.keys(optionalArgs).forEach((key) => {
        if (optionalArgs[key]) {
            tokenStr += ` ${key}=${optionalArgs[key]};`;
        }
    });

    return tokenStr;
}

export function generateState(): string {
    return Math.random().toString(32).slice(2);
}