export interface StringObject {
    [key: string]: string;
}

export interface SummaryValue {
    total: string;
    covered: string;
    skipped: string;
    pct: string;
}

export interface SummaryItem {
    lines: SummaryValue;
    statements: SummaryValue;
    functions: SummaryValue;
    branches: SummaryValue;
}

export interface Summary {
    total: SummaryItem;
    [key: string]: SummaryItem;
}

export interface CoverageItem {
    statement: string;
    line: string;
}
// must container 'total' as key
export interface Coverage {
    [key: string]: CoverageItem;
}

export interface ReleaseOption {
    skipCheck?: boolean;
    skipCheckBranch?: boolean;
    skipTest?: boolean;
    skipVersion?: boolean;
    skipScreenshot?: boolean;
    isMock?: boolean;
    releaseType?: 'docs' | 'lowcode';
}
