type MessageFunc = () => string;
type DefaultFunc = () => void;
type DefaultValue = string | number | boolean | any[] | DefaultFunc;
type DefaultValueFunc = () => DefaultValue[];

export type FrameworkType = 'react' | 'vue';

export interface QuestionType {
    type: 'input' | 'number' | 'confirm' | 'list' | 'rawList' | 'expand' | 'checkbox' | 'password' | 'editor';
    name: string;
    message: string | MessageFunc;
    default: DefaultValue;
    choices?: DefaultValue[] | DefaultValueFunc;
    validate?: () => boolean;
}

export interface InitAnswerType {
    framework: FrameworkType;
}

export interface LoginAnswerType {
    token: string;
}
