export interface TemplateJSON {
    id: string;
    framework: 'vue' | 'react';
    technology: 'es' | 'ts';
    platform: 'mobile' | 'pc';
}

export interface PackageDepItem {
    [dep: string]: string;
}

export interface PackageDep {
    dependencies: PackageDepItem;
    peerDependencies: PackageDepItem;
}

export interface ComponentDepItem {
    dependencies: string;
    version: string;
    isExternal: boolean;
}

export interface ComponentDep {
    [component: string]: {
        tagNameList: string[];
        depList: ComponentDepItem[];
    };
}

export interface MakeEntryTemplatePayload {
    importModuleStr: string;
    importComponentStr: string;
    installComponentStr: string;
    listComponentStr: string;
    listModuleStr: string;
}

export interface ReadNavConfigPayload {
    filePath: string;
    framework: string;
    platform: string;
}
