const inquirer = require('inquirer');

export async function askVersion(prevVersion: string) {
    const questions = [
        {
            type: 'input',
            name: 'version',
            message: `上次发布的版本为${prevVersion}, 本次即将发布的版本为(请填写，填写完成后，将自动commit并push代码):`,
        },
    ];
    const result = await inquirer.prompt(questions);
    return result.version.trim();
}

export async function askGitNotPush() {
    const questions = [
        {
            type: 'confirm',
            name: 'isOk',
            message: `当前git仓库有未push的commit，确定继续发布?`,
        },
    ];
    return inquirer.prompt(questions);
}

