export const getSignature = async () => {
    console.log('--- get signature start---');
    const { Auth } = require('@dp/patriot-sdk');
    const _auth = new Auth('com.sankuai.bfestatic.jointcomponent');
    const signature = await _auth.getTokenSignature('com.sankuai.bfestatic.jointcomponent');
    console.log('--- get signature end ---');
    return signature;
};

