<template>
  <mtd-autocomplete :data="options" v-model="autoValue" placeholder="请输入"
    @search="handleSearch" @select="handleSelect" @change="handleChange" />
</template>
<script>
export default {
  data () {
    return {
      options: [],
      autoValue: '',
    };
  },
  methods: {
    handleSearch (value) {
      this.options = value.split('').map((v, index) => {
        return value.slice(0, index + 1);
      });
    },
    handleSelect (value) {
      console.log('select', value);
    },
    handleChange (value) {
      console.log('change', value);
    },
  },
};
</script>
