import Autocomplete from './autocomplete.vue';
import { withFormItem } from '@components/form-item';

const MtdAutocomplete = withFormItem('MtdAutocomplete', Autocomplete, {
  withRef: true,
  methods: {
    focus () {
      return this.$refs.wrappedInstance.focus();
    },
    blur () {
      return this.$refs.wrappedInstance.blur();
    },
  },
});

/* istanbul ignore next */
MtdAutocomplete.install = function (Vue) {
  Vue.component(MtdAutocomplete.name, MtdAutocomplete);
};
export default MtdAutocomplete;
