<template>
  <div :class="`${prefix}-option-wrap`">
    <slot />
  </div>
</template>
<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';
export default {
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('autocomplete');
    },
  },
  mounted () {
    this.$emit('updated');
  },
  updated () {
    this.$emit('updated');
  },
  destroyed () {
    this.$emit('updated');
  },
};
</script>
