<template>
  <div
    :class="[prefix, _size ? `${prefix}-${_size}` : '']"><slot
  /></div>
</template>
<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
  getIconCls,
  getSize,
} from '@/utils/config';

export default {
  name: 'MtdBreadcrumb',
  props: {
    separator: {
      type: String,
    },
    separatorClass: {
      type: String,
    },
    size: {
      type: String,
      default: '',
    },
  },
  provide () {
    return {
      breadcrumb: this,
    };
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
        getSize,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('breadcrumb');
    },
    _size () {
      return this.config.getSize(this);
    },
  },
};
</script>
