<template>
<div :class="[prefix, shadow ? `${prefix}-${shadow}-shadow` : `${prefix}-always-shadow`]">
  <div :class="[`${prefix}-title`, titleClass]" v-if="$slots.title || title">
    <slot name="title">{{ title }}</slot>
  </div>
  <div :class="`${prefix}-extra`" v-if="$slots.extra">
    <slot name="extra" />
  </div>
  <div :class="[`${prefix}-body`, bodyClass]">
    <slot />
  </div>
</div>
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';
export default {
  name: 'MtdCard',
  props: {
    title: {
      type: String,
    },
    shadow: {
      type: String,
      default: 'always',
    },
    bodyClass: String,
    titleClass: String,
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('card');
    },
  },
};
</script>
