<template>
  <div :class="[prefix, `${prefix}-${index}`, {
    [`${prefix}-active`]: activeIndex === index,
    [`${carouselPrefix}-fake-item`]: fake,
  }]" :style="itemStyle" ref="item">
    <slot />
  </div>
</template>
<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';

export default {
  name: 'MtdCarouselItem',
  props: {
    index: Number,
    activeIndex: Number,
    width: Number,
    height: Number,
    fake: {
      type: Boolean,
      default: false,
    },
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('carousel-item');
    },
    carouselPrefix () {
      return this.config.getPrefixCls('carousel');
    },
    itemStyle () {
      const { width, height } = this;
      return {
        width: width ? `${width}px` : '',
        height: height ? `${height}px` : '',
      };
    },
  },
};
</script>
