<script>
import { applyProps, isSame } from '@/utils/vnode';
import { addResizeListener, removeResizeListener } from '@/utils/resize-event';
import { CONFIG_PROVIDER, getIconCls, getPrefixCls } from '@/utils/config';

function getCarouselItems (vnodes) {
  return (vnodes || []).filter((vnode) => {
    if (vnode.componentOptions) {
      return vnode.componentOptions.Ctor.options.name === 'MtdCarouselItem';
    }
    return false;
  });
}

export default {
  name: 'MtdCarousel',
  props: {
    initialIndex: {
      type: Number,
      default: 0,
    },
    autoplay: {
      type: Boolean,
      default: true,
    },
    indicatorPosition: {
      type: String,
      default: 'inside',
      validator: function (val) {
        return ['inside', 'outside', 'none'].indexOf(val) !== -1;
      },
    },
    speed: {
      type: Number,
      default: 600,
    },
    interval: {
      type: Number,
      default: 3000,
    },
    loop: {
      type: Boolean,
      default: true,
    },
    arrow: {
      type: String,
      default: 'always',
      validator: function (val) {
        return ['always', 'hover', 'never'].indexOf(val) !== -1;
      },
    },
    direction: {
      type: String,
      default: 'horizontally',
      validator (val) {
        return ['horizontally', 'vertical'].indexOf(val) !== -1;
      },
    },
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
      },
    },
  },
  data () {
    return {
      size: 0,
      activeIndex: this.initialIndex || 0,
      isTransition: false,
      items: [],
      isShowNextBtn: true,
    };
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('carousel');
    },
    iconPrefix () {
      return this.config.getIconCls;
    },
    len () {
      return this.items.length;
    },
    activeDot () {
      let activeDot;
      if (this.activeIndex === this.len) {
        activeDot = 0;
      } else if (this.activeIndex === -1) {
        activeDot = this.len - 1;
      } else {
        activeDot = this.activeIndex;
      }
      return activeDot;
    },
    cssProp () {
      return this.direction === 'vertical' ? 'height' : 'width';
    },
    upCssProp () {
      return this.direction === 'vertical' ? 'Height' : 'Width';
    },
    showArrow () {
      return this.direction !== 'vertical';
    },
  },
  watch: {
    autoplay () {
      if (this.autoplay) {
        this.autoplayHandler();
      } else {
        this.clearTimeout();
      }
    },
  },
  created () {
    this.isMouseover = false; // 控制autoplay和next函数
    this.timer = null; // autoplay的时间锚
  },
  mounted () {
    const wrapper = this.$refs.wrapper;
    // 获取容器宽度，容器为100%,拿到真实宽度后渲染children的宽度
    this.size = wrapper && wrapper[`offset${this.upCssProp}`];
    this.calcCarousel();
    this.translated(this.activeIndex + 1);
    setTimeout(() => {
      this.isTransition = true; // tofix: https://tt.sankuai.com/ticket/detail?id=40600798
    }, 0);
    // 当页面宽度改变时，更新width属性
    addResizeListener(this.$el, this.handleWindowResized);
  },
  updated () {
    this.calcCarousel();
  },
  destroyed () {
    removeResizeListener(this.$el, this.handleWindowResized);
  },
  methods: {
    handlePrevBtnClick () {
      this.isMouseover = false;
      this.prev();
    },
    handleNextBtnClick () {
      this.isMouseover = false;
      this.next();
    },
    handleDotClick (index) {
      this.isMouseover = false;
      this.setActiveItem(index);
    },
    handleWindowResized () {
      const wrapper = this.$refs.wrapper;
      const size = wrapper && wrapper[`offset${this.upCssProp}`];
      this.size = size;
      this.setActiveItem(this.activeIndex);
    },
    handleMouseEnter () {
      if (!this.autoplay) {
        return;
      }
      this.isMouseover = true;
      this.autoplayHandler();
    },
    handleMouseLeave () {
      if (!this.autoplay) {
        return;
      }
      this.isMouseover = false;
      this.autoplayHandler();
    },
    setActiveItem (num) {
      this.move(num - this.activeIndex);
    },
    autoplayHandler () {
      if (!this.isMouseover) {
        const nextIndex = (this.activeIndex + 1) % this.len;
        // 修复loop属性不生效，自动播放到最后一张时如果loop属性为false则表示不自动循环，就清空定时器退出自动播放,隐藏next按钮
        if (nextIndex === 0 && !this.loop) {
          clearTimeout(this.timer);
          this.isShowNextBtn = false;
          return;
        }
        this.isShowNextBtn = true;
        this.timer = setTimeout(() => {
          this.next();
          this.autoplayHandler();
        }, this.interval);
      } else {
        this.clearTimeout();
      }
    },
    clearTimeout () {
      this.timer && clearTimeout(this.timer);
      this.timer = null;
    },
    next () {
      if (!this.isMouseover && this.activeIndex < this.len) {
        this.move(1);
      } else {
        this.move(0);
      }
    },
    prev () {
      if (!this.isMouseover && this.activeIndex > -1) {
        this.move(-1);
      } else {
        this.move(0);
      }
    },
    move (num) {
      if (!this.len) {
        return;
      }
      let activeIndex = this.activeIndex;
      const tempActiveIndex = activeIndex;
      activeIndex += num;
      if (activeIndex < 0 || activeIndex >= this.len) {
        if (this.loop) {
          this.activeIndex = activeIndex < 0 ? this.len - 1 : 0;
        } else {
          this.activeIndex = activeIndex = activeIndex < 0 ? 0 : this.len - 1;
        }
      } else {
        this.activeIndex = activeIndex;
      }
      this.translated(activeIndex + 1);
      if (this.activeIndex !== activeIndex) {
        setTimeout(() => {
          this.isTransition = false;
          this.$nextTick(() => {
            // 等 transition 生效
            this.translated(this.activeIndex + 1);
            setTimeout(() => {
              // 等 transform 生效
              this.isTransition = true;
            }, 20);
          });
        }, this.speed);
      }
      if (tempActiveIndex !== activeIndex) {
        this.$emit('change', tempActiveIndex, this.activeIndex);
      }
    },
    translated (num) {
      const { itemList: dom } = this.$refs;
      if (dom) {
        dom.style.transform = this.direction === 'vertical'
          ? `translate3d(0, ${-this.size * num}px, 0)`
          : `translate3d(${-this.size * num}px, 0, 0)`;
      }
    },
    calcCarousel () {
      const items = getCarouselItems(this.$slots.default);
      const changed = !(items.length === this.len &&
        items.every((item, index) => {
          return isSame(item, this.items[index]);
        }));
      if (changed) {
        this.items = items;
        const activeIndex = this.initialIndex || 0;
        this.activeIndex = activeIndex;
        this.setActiveItem(activeIndex);
        if (this.autoplay) {
          this.clearTimeout();
          this.autoplayHandler();
        }
      }
    },
  },
  render () {
    const { prefix, size, arrow, indicatorPosition, activeDot, activeIndex } = this;
    const items = this.len ? this.items.map((i, index) => applyProps(i, {
      index,
      [this.cssProp]: size,
      activeIndex,
    })) : [];
    let liList = [];
    const itemListStyle = {};
    if (this.len) {
      liList = Array.from(new Array(this.len), (val, index) => index);
      itemListStyle[this.cssProp] = `${(this.len + 2) * size}px`;
      if (this.isTransition) {
        itemListStyle.transition = `transform ${this.speed / 1000}s ease-in-out`;
      }
      const firstSlot = items[0];
      const lastSlot = items[this.len - 1];

      const firstSlotFaker = applyProps(firstSlot, {
        fake: true,
        index: -1,
      });
      const lastSlotFaker = applyProps(lastSlot, {
        fake: true,
        index: this.len,
      });
      firstSlotFaker.key = `firstFaker-${firstSlotFaker.key}`;
      lastSlotFaker.key = `lastFaker-${lastSlotFaker.key}`;
      items.push(firstSlotFaker);
      items.unshift(lastSlotFaker);
    }
    return (
      <div class={[prefix, `${prefix}-${this.direction}`]} ref={'wrapper'} onMouseenter={this.handleMouseEnter}
        onMouseleave={this.handleMouseLeave}>
        <div class={`${prefix}-itemList-outer`}>
          {
            <div class={`${prefix}-itemList`} style={itemListStyle} ref={'itemList'}>
              {size ? items : null}
            </div>
          }
        </div>
        {this.showArrow && <button
          class={[`${prefix}-btn`, `${prefix}-prev`, `${prefix}-${arrow}`]} onClick={this.handlePrevBtnClick}
        >
          <i class={this.iconPrefix('left')} />
        </button> }
        {this.isShowNextBtn && this.showArrow && <button
          class={[`${prefix}-btn`, `${prefix}-next`, `${prefix}-${arrow}`]} onClick={this.handleNextBtnClick}
        >
          <i class={this.iconPrefix('right')} />
        </button>}
        {indicatorPosition !== 'none' && <ul class={[`${prefix}-indicator`, `${prefix}-${indicatorPosition}`]}
        >
          {
            liList.map(item => {
              return (
                <span class={[`${prefix}-dot`, { [`${prefix}-active-dot`]: activeDot === item }]}
                  onClick={() => this.handleDotClick(item)}
                />
              );
            })
          }
        </ul>}
      </div>
    );
  },
};
</script>
