import Cascader from './cascader.vue';
import { withFormItem } from '@components/form-item';
import CreateComponent from './control';

const MtdCascader = withFormItem(
  'MtdCascader',
  CreateComponent(Cascader),
  {
    withRef: true,
    noLoading: true,
    methods: {
      getNodes (values) {
        return this.$refs.wrappedInstance.getNodes(values);
      },
      getNode (value) {
        return this.$refs.wrappedInstance.getNode(value);
      },
      focus () {
        return this.$refs.wrappedInstance.focus();
      },
      blur () {
        return this.$refs.wrappedInstance.blur();
      },
    },
  },
);

/* istanbul ignore next */
MtdCascader.install = function (Vue) {
  Vue.component(MtdCascader.name, MtdCascader);
};

export default MtdCascader;
