import { mount } from '@vue/test-utils';
import CollapseItem from '../collapse-item.vue';

describe('Collapse Item', function () {
  it('create', () => {
    const wrapper = mount(CollapseItem);
    expect(wrapper.vm).to.exist;
  });

  it('title', function () {
    const wrapper = mount(CollapseItem, {
      propsData: {
        title: '折叠面板标题1',
        value: '1'
      }
    });
    expect(wrapper.find('.mtd-collapse-item-header').text()).to.be.eql('折叠面板标题1')
  });

  it('title slot', function () {
    const wrapper = mount(CollapseItem, {
      propsData: {
        value: '1'
      },
      slots: {
        title: '<div slot="title" class="custom-collapse-title">xxxxx</div>'
      }
    });
    expect(wrapper.find('.mtd-collapse-item-header > .custom-collapse-title').exists()).to.be.true;
  });
});
