<template>
  <Popper v-bind="$attrs"
    :class="[
      prefix,
      _size ? `${prefix}-${_size}` : ''
    ]"
    trigger="click"
    :placement="placement"
    :visible="visible"
    :show-arrow="showArrow"
    :append-to-container="appendToContainer"
    :get-popup-container="getPopupContainer"
    @clickoutside="handleClickoutside"
  >
    <Reference>
      <div :class="`${prefix}-wrapper`">
        <div :class="`${prefix}-mask`" v-if="disabled" />
        <div :class="[
            `${prefix}-trigger`,
            disabled ? `${prefix}-disabled` : ''
          ]"
          @click="handleTrigger"
        >
          <span :class="{
              [`${prefix}-color`]: true,
              'is-alpha': showAlpha
            }"
          >
            <span :class="`${prefix}-color-inner`"
              :style="{
                backgroundColor: displayedColor
              }" />
            <span
              :class="`${prefix}-empty ${iconPrefix('close-thick')}`"
              v-if="!value && !showPanelColor"
            />
          </span>
          <span
            :class="`${prefix}-icon ${iconPrefix('down')}`"
            v-show="value || showPanelColor"
          />
        </div>
      </div>
    </Reference>
    <Drop :class="[`${prefix}-panel`, popperClass || '']">
      <picker-dropdown
        ref="dropdown"
        @pick="confirmValue"
        @clear="clearValue"
        :color="color"
        :show-alpha="showAlpha"
        :predefine="predefine" />
    </Drop>
  </Popper>
</template>

<script>
import Color from './utils/color';
import PickerDropdown from './components/picker-dropdown.vue';
import { Popper, Drop, Reference } from '@components/popper';
import {
  CONFIG_PROVIDER,
  getPrefixCls,
  getIconCls,
  getSize,
} from '@/utils/config';

export default {
  name: 'MtdColorPicker',
  components: {
    PickerDropdown,
    Popper,
    Drop,
    Reference,
  },
  props: {
    value: String,
    showAlpha: Boolean,
    colorFormat: String,
    disabled: Boolean,
    size: String,
    popperClass: String,
    predefine: Array,
    showArrow: {
      type: Boolean,
      default: false,
    },
    placement: {
      type: String,
      default: 'bottom-start',
    },
    appendToContainer: {
      type: Boolean,
      default: true,
    },
    getPopupContainer: Function,
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
        getSize,
      },
    },
  },
  data () {
    const color = new Color({
      enableAlpha: this.showAlpha,
      format: this.colorFormat,
    });

    return {
      color,
      showPicker: false,
      showPanelColor: false,
    };
  },

  computed: {
    prefix () {
      return this.config.getPrefixCls('color-picker');
    },
    _size () {
      return this.config.getSize(this);
    },
    iconPrefix () {
      return this.config.getIconCls;
    },
    displayedColor () {
      if (!this.value && !this.showPanelColor) {
        return 'transparent';
      }

      return this.displayedRgb(this.color, this.showAlpha);
    },
    visible () {
      return !this.disabled && this.showPicker;
    },
  },

  watch: {
    value (val) {
      if (!val) {
        this.showPanelColor = false;
      } else if (val && val !== this.color.value) {
        this.color.fromString(val);
      }
    },
    color: {
      deep: true,
      handler () {
        this.showPanelColor = true;
      },
    },
    displayedColor (val) {
      if (!this.showPicker) return;
      const currentValueColor = new Color({
        enableAlpha: this.showAlpha,
        format: this.colorFormat,
      });
      currentValueColor.fromString(this.value);

      const currentValueColorRgb =
        this.displayedRgb(currentValueColor, this.showAlpha);
      if (val !== currentValueColorRgb) {
        this.$emit('active-change', val);
      }
    },
    visible (visible) {
      this.$emit('update:visible', visible);
    },
  },

  mounted () {
    const value = this.value;
    if (value) {
      this.color.fromString(value);
    }
  },

  methods: {
    handleClickoutside () {
      this.showPicker = false;
    },
    handleTrigger () {
      if (this.disabled) return;
      this.showPicker = !this.showPicker;
    },
    confirmValue () {
      const value = this.color.value;
      this.$emit('input', value);
      this.$emit('change', value);
      this.showPicker = false;
    },
    clearValue () {
      this.$emit('input', null);
      this.$emit('change', null);
      this.showPanelColor = false;
      this.showPicker = false;
      this.resetColor();
    },
    hide () {
      this.showPicker = false;
      this.resetColor();
    },
    resetColor () {
      this.$nextTick(_ => {
        if (this.value) {
          this.color.fromString(this.value);
        } else {
          this.showPanelColor = false;
        }
      });
    },
    displayedRgb (color, showAlpha) {
      if (!(color instanceof Color)) {
        throw Error('color should be instance of Color Class');
      }

      const { r, g, b } = color.toRgb();
      return showAlpha
        ? `rgba(${r}, ${g}, ${b}, ${color.get('alpha') / 100})`
        : `rgb(${r}, ${g}, ${b})`;
    },
  },
};
</script>
