<template>
  <section
    :class="{
      [prefix]: true,
      [`${prefix}-vertical`]: isVertical
    }">
    <slot />
  </section>
</template>

<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';

export default {
  name: 'MtdContainer',

  componentName: 'mtd-Container',

  props: {
    direction: String,
  },

  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },

  computed: {
    prefix () {
      return this.config.getPrefixCls('container');
    },
    isVertical () {
      if (this.direction === 'vertical') {
        return true;
      } else if (this.direction === 'horizontal') {
        return false;
      }
      return this.$slots && this.$slots.default
        ? this.$slots.default.some(vnode => {
          const tag = vnode.componentOptions && vnode.componentOptions.tag;
          return tag === 'mtd-header' || tag === 'mtd-footer';
        })
        : false;
    },
  },
};
</script>
