<template>
  <div class="demo-picker-group">
    <mtd-date-picker type="date"
      v-model="value1" confirm :open.sync="open"
      placeholder="选择时间">
      <template v-slot:confirm>
        <mtd-button-group>
          <mtd-button type="text" @click="clear">清空</mtd-button>
          <mtd-button type="primary" @click="submit">确定</mtd-button>
        </mtd-button-group>
      </template>
    </mtd-date-picker>
  </div>
</template>
<script>
export default {
  data () {
    return {
      value1: new Date(),
      open: false,
    };
  },
  methods: {
    clear () {
      this.value1 = undefined;
      this.open = false;
    },
    submit () {
      this.open = false;
    },
  },
};
</script>
