<template>
  <ul
    :class="{
      [prefix]: true,
      [`${prefix}-${_size}`]: _size }
    ">
    <slot />
  </ul>
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
  getSize,
} from '@/utils/config';
export default {
  name: 'MtdDropdownMenu',
  props: {
    size: String,
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getSize,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('dropdown-menu');
    },
    _size () {
      return this.config.getSize(this);
    },
  },
};
</script>
