<template>
  <i :class="iconPrefix(name)" v-on="$listeners" />
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
  getIconCls,
} from '@/utils/config';

export default {
  name: 'MtdIcon',
  props: {
    name: {
      type: String,
      required: true,
    },
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('icon');
    },
    iconPrefix () {
      return this.config.getIconCls;
    },
  },
};
</script>
