<template>
  <span
    :class="{
      [prefix]: true,
      [`${prefix}-compact`]: compact,
    }"
    v-on="$listeners"
  >
    <div :class="`${prefix}-prepend`" v-if="$slots.prepend">
      <slot name="prepend" />
    </div>
    <slot />
    <div :class="`${prefix}-append`" v-if="$slots.append">
      <slot name="append" />
    </div>
  </span>
</template>

<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';

export default {
  name: 'MtdInputGroup',
  props: {
    compact: Boolean,
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('input-group');
    },
  },
};
</script>
