import { mount } from '@vue/test-utils';
import InputGroup from '../input-group.vue';

describe('input-group', () => {
  it('compact', () => {
    const wrapper = mount(InputGroup, {
      propsData: {
        compact: true
      }
    });
    expect(wrapper.classes()).to.include(`mtd-input-group-compact`);
  });
  it('mouseenter/mouseleave/focus/blur called', () => {
    const events = ['mouseenter', 'mouseleave', 'focus', 'blur'];
    const wrapper = mount(InputGroup);
    events.forEach(event => {
      wrapper.vm.$emit(event);
      expect(wrapper.emitted()[event].length).to.equal(1);
    });
  });
});
