<template>
  <div :class="prefix">
    <div :class="`${prefix}-header`" v-if="$slots.header || header">
      <slot name="header">{{ header }}</slot>
    </div>
    <slot />
  </div>
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';

export default {
  name: 'MtdListItemGroup',
  props: {
    header: {
      type: String,
    },
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('list-item-group');
    },
  },
};
</script>
