import { mount } from '@vue/test-utils';
import ListItemGroup from '../index';

describe('ListItemGroup', function () {
  it('create', () => {
    const wrapper = mount(ListItemGroup);
    expect(wrapper.vm).to.exist;
  });
  it('header', () => {
    const wrapper = mount(ListItemGroup, {
      propsData: {
        header: 'test'
      }
    })
    expect(wrapper.find('.mtd-list-item-group-header')).to.be.ok;
  })
  it('slot header', () => {
    const wrapper = mount(ListItemGroup, {
      slots: {
        header: '<span slot="header"><span class="test-slot"/></span>'
      }
    });
    expect(wrapper.find('.mtd-list-item-group-header')).to.be.ok;
  })
});
