<template>
  <div :class="prefix" v-on="$listeners">
    <slot />
  </div>
</template>
<script>
import { CONFIG_PROVIDER,
  getPrefixCls,
} from '@/utils/config';
export default {
  name: 'MtdListItem',
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('list-item');
    },
  },
};
</script>
