<template>
  <div :class="wrapperCls" v-on="$listeners">
    <slot />
    <slot
      name="loadMore"
      v-if="!loading"
    />
    <div
      v-else
      :class="`${prefix}-loading`"
    >
      <mtd-icon-button :icon="iconPrefix('loading')" />
    </div>
  </div>
</template>
<script>
import {
  CONFIG_PROVIDER,
  getPrefixCls,
  getIconCls,
  getSize,
} from '@/utils/config';
import MtdIconButton from '@components/icon-button';

export default {
  name: 'MtdList',
  components: {
    MtdIconButton,
  },
  props: {
    size: {
      type: String,
      default: 'normal',
    },
    loading: {
      type: Boolean,
      default: false,
    },
  },
  inject: {
    config: {
      from: CONFIG_PROVIDER,
      default: {
        getPrefixCls,
        getIconCls,
        getSize,
      },
    },
  },
  computed: {
    prefix () {
      return this.config.getPrefixCls('list');
    },
    _size () {
      return this.config.getSize(this);
    },
    iconPrefix () {
      return this.config.getIconCls;
    },
    wrapperCls () {
      return [
        `${this.prefix}`,
        {
          [`${this.prefix}-${this._size}`]: this._size,
        },
      ];
    },
  },
};
</script>
