import { mount } from '@vue/test-utils';
import List from '../index';

describe('List', function () {
  it('create', () => {
    const wrapper = mount(List);
    expect(wrapper.vm).to.exist;
  });
  it('size', function() {
    const sizes = ['small', 'large'];
    sizes.forEach((size) => {
      const wrapper = mount(List, {
        propsData: {
          size
        }
      })
      expect(wrapper.classes()).to.includes(`mtd-list-${size}`);
    });
  });
  it('loading', function() {
    const wrapper = mount(List, {
      propsData: {
        loading: true
      }
    });
    expect(wrapper.find('.mtd-list-loading').exists()).to.be.true;
  })
  it('slot loadMore', () => {
    const wrapper = mount(List, {
      slots: {
        loadMore: '<span slot="loadMore"><span class="test-slot"/></span>'
      }
    })
    expect(wrapper.find('.test-slot')).to.be.ok
  })
})
